INSERT INTO Miel (nom, description, type_miel, prix, stock) VALUES ('Miel de Fleurs Sauvages', 'Ce miel provient d''un mélange riche et varié de nectars de différentes fleurs sauvages, offrant une symphonie de saveurs qui reflète la biodiversité des prairies non cultivées. Chaque cuillère transporte vos sens à travers un champ floral ensoleillé.', 'MIEL_DE_FLEURS_SAUVAGES', 12.00, 25);
INSERT INTO Miel (nom, description, type_miel, prix, stock) VALUES ('Miel d''Acacia', 'Le miel d''Acacia est apprécié pour sa clarté cristalline et sa douceur subtile. Ce miel léger est parfait pour sucrer vos boissons et plats sans en altérer le goût, conservant ainsi leur authenticité aromatique.', 'MIEL_D_ACACIA', 15.00, 30);
INSERT INTO Miel (nom, description, type_miel, prix, stock) VALUES ('Miel de Lavande', 'Avec son arôme floral distinctif et ses notes fruitées, le miel de lavande est une invitation à la relaxation. Idéal pour agrémenter des thés ou comme composant de soins de beauté naturels, il transporte les bienfaits apaisants de la lavande.', 'MIEL_DE_LAVANDE', 20.00, 20);
INSERT INTO Miel (nom, description, type_miel, prix, stock) VALUES ('Miel de Thym', 'Ce miel bénéficie des propriétés antiseptiques du thym, le rendant parfait pour le traitement des maux de gorge et renforcer le système immunitaire. Son goût riche et intense en fait un excellent ajout à la cuisine méditerranéenne.', 'MIEL_DE_THYM', 22.50, 15);
INSERT INTO Miel (nom, description, type_miel, prix, stock) VALUES ('Miel de Manuka', 'Connu mondialement pour ses propriétés antibactériennes uniques, le miel de Manuka est un trésor de Nouvelle-Zélande. Il est utilisé aussi bien dans les soins de santé que comme un luxueux édulcorant, apportant une touche de luxe à chaque utilisation.', 'MIEL_DE_MANUKA', 35.00, 10);
INSERT INTO Miel (nom, description, type_miel, prix, stock) VALUES ('Miel de Sarrasin', 'Avec sa richesse en minéraux et son goût fort et distinctement terrestre, le miel de sarrasin est un favori pour ceux qui apprécient une saveur robuste. Il est particulièrement apprécié dans la confection de pains d''épices et autres pâtisseries.', 'MIEL_DE_SARRASIN', 18.00, 12);
INSERT INTO Miel (nom, description, type_miel, prix, stock) VALUES ('Miel de Citronnier', 'Ce miel capte l''essence légère et rafraîchissante des citronniers en fleur. Avec ses subtiles touches d''agrumes, il est parfait pour éclaircir les thés ou comme garniture sur des desserts frais.', 'MIEL_DE_CITRONNIER', 17.00, 20);
INSERT INTO Miel (nom, description, type_miel, prix, stock) VALUES ('Miel d''Eucalyptus', 'Avec ses saveurs profondément boisées et un soupçon de menthol, le miel d''Eucalyptus est reconnu pour ses bienfaits respiratoires. Il apporte une touche exotique aux plats et peut aider à apaiser les symptômes du rhume.', 'MIEL_D_EUCALYPTUS', 16.00, 18);
INSERT INTO Miel (nom, description, type_miel, prix, stock) VALUES ('Miel de Châtaignier', 'Ce miel a un goût unique et puissant, avec une légère amertume qui le distingue. Riche en pollen, il est souvent recherché pour ses propriétés stimulantes sur la circulation sanguine.', 'MIEL_DE_CHATAIGNIER', 19.00, 14);
INSERT INTO Miel (nom, description, type_miel, prix, stock) VALUES ('Miel de Tournesol', 'Léger et délicat, ce miel possède une couleur jaune vive qui évoque les champs de tournesols en été. Son goût doux le rend idéal pour sucrer sans écraser les saveurs des aliments.', 'MIEL_DE_TOURNESOL', 12.50, 30);
INSERT INTO Miel (nom, description, type_miel, prix, stock) VALUES ('Miel de Forêt', 'Issu de la diversité des plantes forestières, ce miel mélange les saveurs des bois dans un équilibre parfait. Il est souvent utilisé pour ses qualités tonifiantes et son goût profond qui rappelle les promenades en forêt.', 'MIEL_DE_FORET', 21.00, 16);
INSERT INTO Miel (nom, description, type_miel, prix, stock) VALUES ('Miel de Montagne', 'Collecté dans les hauteurs vierges, ce miel riche en saveurs florales capture l''essence des montagnes. Il est apprécié pour sa pureté et son goût complexe, reflétant la diversité des fleurs alpines.', 'MIEL_DE_MONTAGNE', 23.00, 11);
